import React, { useState } from 'react';
import EventsList from './components/EventsList';
import './App.css';

function App() {
  const [events, setEvents] = useState([]);

  function fetchEventsHandler() {
    fetch(
      'https://cors-anywhere.herokuapp.com/https://thedistance.co.uk/wp-content/uploads/2020/01/eventbrite.json',
      {
        method: 'GET',
        mode: 'cors',
      }
    )
      .then((response) => response.json())
      .then((data) => {
        return setEvents(data.events);
      });
    // .then((response) => {
    //   return response.json();
    // })
    // .then((data) => {
    //   setEvents(data.events);
    // });
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchEventsHandler}>Show Events</button>
      </section>
      <section>
        <EventsList events={events} />
      </section>
    </React.Fragment>
  );
}

export default App;
